import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Yönlendirme için kullan
import './App.css';

const Login = () => {
    const [tckn, setTckn] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');
    const navigate = useNavigate(); // Yönlendirme için kullan
    
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('/api/login', {
                tcknVkn: tckn,
                password: password
            });

            if (response.status === 200) {
                // Başarılı giriş sonrası yapılacak işlemler
                // Örneğin, kredi başvuru sayfasına yönlendirme
                navigate('/loan-application'); // Replace with your loan application route
            }
        } catch (error) {
            console.error('Giriş sırasında hata meydana geldi!', error);
            setLoginError('Kullanıcı adı veya parola yanlış.'); 
        }
    };

    const handleRegisterRedirect = () => {
        navigate('/register'); // Kayıt sayfasına yönlendirme
    };

    return (
        <div>
            <h1 align="center">LOGIN</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={tckn}
                    onChange={(e) => setTckn(e.target.value)}
                    placeholder="TCKN"
                    required
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Parola"
                    required
                />
                <button type="submit">Giriş Yap</button>
            </form>
            {loginError && (
                <p className="error">{loginError}</p>
            )}
            <button onClick={handleRegisterRedirect}>Kullanıcı Oluştur</button>
        </div>
    );
};

export default Login;
