import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // react-router-dom kullanarak yönlendirme yapılır
import './App.css';

const Login = () => {
    const [tckn, setTckn] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');
    const navigate = useNavigate(); // Yönlendirme için hook

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('/api/users/login', { tckn, password });
            // Başarılı giriş sonrası yapılacak işlemler (örneğin token saklama vs.)
            console.log('Giriş başarılı:', response.data);
            setLoginError('');
            // Giriş başarılıysa ana sayfaya yönlendirme veya başka bir işlem
        } catch (error) {
            console.error('Giriş sırasında hata meydana geldi!', error);
            setLoginError('Kullanıcı adı veya parola yanlış.'); // Hata mesajı
        }
    };

    const handleRegisterRedirect = () => {
        navigate('/register'); // Kayıt sayfasına yönlendirme
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={tckn}
                    onChange={(e) => setTckn(e.target.value)}
                    placeholder="TCKN"
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Parola"
                />
                <button type="submit">Giriş Yap</button>
            </form>
            {loginError && (
                <p className="error">{loginError}</p>
            )}
            &nbsp;
            <button onClick={handleRegisterRedirect}>Kullanıcı Oluştur</button>
        </div>
    );
};

export default Login;
