import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const CreateUser = () => {
    const [companyName, setCompanyName] = useState('');
    const [tcknVkn, setTcknVkn] = useState('');
    const [address, setAddress] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userCode, setUserCode] = useState(null);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('/api/users/register', {
                companyName: companyName,
                tcknVkn: tcknVkn,
                address: address,
                email: email,
                password: password
            });

            if (response.status === 200) {
                // Kullanıcı oluşturulduktan sonra kullanıcı kodunu göster
                setUserCode(response.data.userCode);
            }
        } catch (error) {
            console.error('Kullanıcı oluşturma sırasında hata meydana geldi!', error);
        }
    };

    return (
        <div>
            <h1 align="center">REGISTER</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Firma Adı"
                    required
                />
                <input
                    type="text"
                    value={tcknVkn}
                    onChange={(e) => setTcknVkn(e.target.value)}
                    placeholder="TCKN/VKN"
                    required
                />
                <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    placeholder="Adres"
                    required
                />
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                    required
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Parola"
                    required
                />
                <button type="submit">Kaydet</button>
            </form>
            {userCode && (
                <p>Kullanıcı Kodu: {userCode}</p>
            )}
        </div>
    );
};

export default CreateUser;
