import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const NewApplication = () => {
  const { userCode } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [campaigns,setCampaigns]=useState([]);
  const [formData, setFormData] = useState({
    tckn: "",
    phoneNumber: "",
    firstName: "",
    lastName: "",
    email: "",
    address: "",
    monthlyIncome: "",
    loanAmount: "",
    campaignName: "",
    termLoan: "",
    interestRate: "",
  });

  useEffect(() => {
    if (!userCode) {
      navigate("/loans");
      return;
    }
    setLoading(true);
    fetch('http://localhost:8080/api/campaign')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setCampaigns(data))
      .catch((error) => {
        console.error("Error fetching campaign data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [userCode, navigate]);

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <div>
      <form>
        <input
          type="text"
          placeholder="TCKN"
          value={formData.tckn}
          onChange={(e) => setFormData({ ...formData, tckn: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Telefon"
          value={formData.phoneNumber}
          onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Ad"
          value={formData.firstName}
          onChange={(e) =>
            setFormData({ ...formData, firstName: e.target.value })
          }
          required
        />
        <input
          type="text"
          placeholder="Soyad"
          value={formData.lastName}
          onChange={(e) =>
            setFormData({ ...formData, lastName: e.target.value })
          }
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Adres"
          value={formData.address}
          onChange={(e) =>
            setFormData({ ...formData, address: e.target.value })
          }
          required
        />
        <input
          type="number"
          placeholder="Aylık Gelir"
          value={formData.monthlyIncome}
          onChange={(e) =>
            setFormData({ ...formData, monthlyIncome: e.target.value })
          }
          required
        />
        <input
          type="number"
          placeholder="Kredi Tutarı"
          value={formData.loanAmount}
          onChange={(e) =>
            setFormData({ ...formData, loanAmount: e.target.value })
          }
          required
        />
        
      </form>
      {/* <button onClick={handleSubmit}>BAŞVURU AL</button> */}
    </div>
    <select value={campaigns} onChange={(e)=>setCampaigns(e.target.value)}>
      <option value="">Bir Kampanya Seçimi Yapın</option>
      {campaign.map((campaigns)=>(
        <option key={campaigns.name} value={campaigns.name}>
          {campaigns.name}
        </option>
      ))}
      </select>
    </div>
    

  );
};
export default NewApplication;
