import React, {useEffect, useState} from "react";
import { useNavigate } from "react-router-dom";

const loanTable = () =>{
  const { userCode } = useParams();
  const navigate = useNavigate();
  const [barrowerData, setBarroworData]=useState([]);
  const [loading,setLoading]=state(false);
  useEffect(()=>{
    setLoading(true);
    fetch(`http://localhost:8080/api/loans${userCode}`)
    .then(response => response.json())
    .then(json => setBarroworData(json))
    .finally(()=>{
      setLoading(false)
    })
  },[])

  return (
    <div>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <h1>Kredi Tablosu</h1>
          <table border={1}>
            <tr>
              <th>isim soyisim</th>
              <th>kampanya adı</th>
              <th>kredi tarihi</th>
              <th>kredi tutarı</th>
              <th>kredi oranı</th>
            </tr>
            {barrowerData.map(barrower => (
              <tr key={barrower.id}>
                <td>{barrower.nameSurname}</td>
                <td>{barrower.campaignName}</td>
                <td>{barrower.loanDate}</td>
                <td>{barrower.loanAmount}</td>
                <td>{barrower.loanRate}</td>
              </tr>
            ))}
          </table>
        </>
      )}
    </div>
  )

};

export default loanTable;
