import React, { useState, useEffect } from 'react';
import useApi from './useApi/useApi';
import { fetchLoansUrl } from './apiUrl/apiUrl';

const LoanTable = () => {
    const { post, data, error, loading } = useApi('/loans'); // API endpoint'i doğru ayarlayın
    const [loans, setLoans] = useState([]);
    const [fetchError, setFetchError] = useState('');

    const fetchLoans = async () => {
        try {
            await post(); // Boş body ile POST isteği gönder
            if (error) {
                setFetchError('Kredileri getirirken hata meydana geldi!');
            } else {
                setLoans(data);
            }
        } catch (error) {
            console.error('Kredileri getirirken hata meydana geldi!', error);
            setFetchError('Kredileri getirirken hata meydana geldi!');
        }
    };

    useEffect(() => {
        fetchLoans();
    }, []); // Boş bağımlılık dizisi, bileşen ilk yüklendiğinde çalışır

    if (loading) return <p>Yükleniyor...</p>;

    return (
        <div>
            <h1>Kredi Tablosu</h1>
            {fetchError && <p className="error">{fetchError}</p>}
            {/* Kredileri listeleme tablosu buraya gelecek */}
        </div>
    );
};

export default LoanTable;
