import React, { useState } from 'react';
import axios from 'axios';
import './App.css'
const CreateUser = () => {
    const [companyName, setCompanyName] = useState('');
    const [tcknVkn, setTcknVkn] = useState('');
    const [address, setAddress] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userCode, setUserCode] = useState(null);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const user = { companyName, tcknVkn, address, email, password };
        try {
            const response = await axios.post('/api/users/create', user);
            setUserCode(response.data.userCode);
        } catch (error) {
            console.error('Kullanıcı oluştururken hata meydana geldi!', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Firma Adı"
                />
                <input
                    type="text"
                    value={tcknVkn}
                    onChange={(e) => setTcknVkn(e.target.value)}
                    placeholder="TCKN/VKN"
                />
                <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    placeholder="Adres"
                />
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Parola"
                />
                <button type="submit">Kaydet</button>
            </form>
            {userCode && (
                <p>Kullanıcı Kodu: {userCode}</p>
            )}
        </div>
    );
};
export default CreateUser;
