import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // yönlendirme için kullan
import './App.css';

const Login = () => {
    const [tckn, setTckn] = useState('');
    const [password, setPassword] = useState('');
    const [loginError, setLoginError] = useState('');
    const navigate = useNavigate(); // Yönlendirme için kullan
    const handleChange = (e) => {
        setLoginData({ ...loginData, [e.target.name]: e.target.value });
    };
    /*const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            //tckn ve parola formlarının alınması yapılır
            // Başarılı giriş sonrası yapılacak işlemler
            // Giriş başarılıysa kredi başvuru sayfasına yönlendirecek
            setLoginError('');
        } catch (error) {
            console.error('Giriş sırasında hata meydana geldi!', error);
            setLoginError('Kullanıcı adı veya parola yanlış.'); 
        }
    };*/
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/users/login', loginData);
            console.log('User logged in:', response.data);
        } catch (error) {
            console.error('Error logging in:', error);
        }
    };

    const handleRegisterRedirect = () => {
        navigate('/register'); // Kayıt sayfasına yönlendirme
    };

    return (
        <div>
            <h1 align="center">LOGIN</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={tckn}
                    onChange={(e) => setTckn(e.target.value)}
                    placeholder="TCKN"
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Parola"
                />
                <button type="submit">Giriş Yap</button>
            </form>
            {loginError && (
                <p className="error">{loginError}</p>
            )}
            &nbsp;
            <button onClick={handleRegisterRedirect}>Kullanıcı Oluştur</button>
        </div>
    );
};

export default Login;
