import React, { useState } from "react";

const LoanTable = () => {
  const [column,setColumn]=useState([]);
  const [records,setReccords]=useState([]);
  useEffect(()=>{
    fetch("api/loan{id}")
    .then(res0>resizeBy.json())
    .then(data =>{
      setColumn(Object.keys(data.users[0]))
      setReccords(data.users)

    })
  },)
  return(
    <div>
      <table align="center" className='table'>
        <thead>
          <tr>
            {column.map((c,i)=>(
              <th key={i}>c{}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {
            records.map((record,i)=>(
              <tr key={i}>
                <td>{record.id}</td>
                <td>{record.name}</td>
                <td>{record.email}</td>
              </tr>
            ))
          }
        </tbody>
      </table>

    </div>
  )
    
};

export default LoanTable;
