import React, {useState, useEffect} from 'react';
import {useParams} from 'react-router-dom';
import useApi from './useApi/useApi';
import { loans } from './apiUrl/apiUrl';


const LoanTable = () =>{
  const{userCode} = useParams();
  const {get, response}=useApi();
  const [loans,setLoans]=useState([]);
  const [error,setError]=useState('');

  useEffect(()=>{

    const fetchLoans =async()=>{
      try{
        const res=await get(`${loans}/${userCode}`);
        if(res.ok){
          const loanData = await res.json();
          setLoans(loans);
        }else{
          console.log("hata",res.status);
          setError('Krediler Tablosu getirilemedi');
        }

      }catch(error){
        console.error('Krediları getirirken hata meydana geldi!', error);
        setError('Kredirken bir hata oluştu. Lütfen tekrar deneyin.');

      }
    };

    fetchLoans();
  }, [userCode]);
  return (
    <div>
        <h1 align="center">Kredi Tablosu</h1>
        {error && <p className="error">{error}</p>}
        <table>
            <thead>
                <tr>
                    <th>Adı Soyadı</th>
                    <th>Kampanya Adı</th>
                    <th>Kredi Tarihi</th>
                    <th>Kredi Miktarı</th>
                    <th>Kredi Oranı</th>
                </tr>
            </thead>
            <tbody>
                {loans.length > 0 ? (
                    loans.map((loan, index) => (
                        <tr key={index}>
                            <td>{loan.nameSurname}</td>
                            <td>{loan.campaignName}</td>
                            <td>{loan.loanDate}</td>
                            <td>{loan.loanAmount}</td>
                            <td>{loan.loanRate}</td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="5">Kredi bulunamadı.</td>
                    </tr>
                )}
            </tbody>
        </table>
    </div>
);
  
}

export default LoanTable;
