// export default useApi;
import { BASE_API_URL } from '../apiUrl/apiUrl';
import { useFetch } from 'use-http';

const useApi = (endpoint, options = {}) => {
  const { get, post, data, error, loading } = useFetch(`${BASE_API_URL}${endpoint}`, options, triggerDependency);

  return {
    get,
    post: (body) => post(body),
    put: (body) => put(body),
    del,
    data,
    error,
    loading
  };
};

export default useApi;
