import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const LoanTable = () => {
  const { userCode } = useParams();
  const [borrowerData, setBorrowerData] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:8080/api/loans/${userCode}`)
      .then(response => response.json())
      .then(json => setBorrowerData(json))
      .finally(() => setLoading(false));
  }, [userCode]);
  console.log('User Code:', userCode); // Debugging log


  return (
    <div>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <h1>Kredi Tablosu</h1>
          <table border={1}>
            <thead>
              <tr>
                <th>isim soyisim</th>
                <th>kampanya adı</th>
                <th>kredi tarihi</th>
                <th>kredi tutarı</th>
                <th>kredi oranı</th>
              </tr>
            </thead>
            <tbody>
              {borrowerData.map(borrower => (
                <tr key={borrower.id}>
                  <td>{borrower.nameSurname}</td>
                  <td>{borrower.campaignName}</td>
                  <td>{borrower.loanDate}</td>
                  <td>{borrower.loanAmount}</td>
                  <td>{borrower.loanRate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default LoanTable;
