// export default LoanTable;
const LoanTable = () => {
  const { userCode } = useParams(); // Use useParams to get userCode from URL
  const [barrowerData, setBarrowerData] = useState([]);

  const fetchBarrowerData = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/loans/${userCode}`); // Correct endpoint
      const data = await response.json();
      setBarrowerData(data);
    } catch (error) {
      console.error("Veriler alınamadı: ", error);
    }
  };

  const handleNewApplicationRedirect = () => {
    navigate("/newApplication");
  };

  useEffect(() => {
    fetchBarrowerData();
  }, [userCode]); // Include userCode in dependencies

  return (
    <div>
      <h1 align="center">Kredi Bilgi Listesi</h1>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "10px",
        }}
      >
        <button onClick={handleNewApplicationRedirect}>Yeni Başvuru</button>
        <button onClick={fetchBarrowerData}>Müşterileri Listele</button>
      </div>
      <table style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Ad Soyad</th>
            <th>Kampanya Adı</th>
            <th>Kredi Tarihi</th>
            <th>Kredi Tutarı</th>
            <th>Kredi Oranı</th>
          </tr>
        </thead>
        <tbody>
          {barrowerData.map((obj) => (
            <tr key={obj.id}>
              <td>{obj.nameSurname}</td>
              <td>{obj.campaignName}</td>
              <td>{obj.loanDate}</td>
              <td>{obj.loanAmount}</td>
              <td>{obj.loanRate}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default LoanTable;
