import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const NewApplication = () => {
  const { userCode } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    tckn: "",
    phoneNumber: "",
    firstName: "",
    lastName: "",
    email: "",
    address: "",
    monthlyIncome: "",
    loanAmount: "",
    campaignName: "",
    termLoan: "",
    interestRate: "",
  });

  const [campaign, setCampaign] = useState([]);

  useEffect(() => {
    fetch(`/api/newApplication`)  // Ensure this matches your backend API
      .then((response) => response.json())
      .then((data) => setCampaign(data));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    fetch("/api/loans", {  // Ensure this matches your backend API
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setLoading(false);
        navigate(`/loans/${userCode}`);
      })
      .catch((error) => {
        setLoading(false);
        console.error("There was an error submitting the application!", error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Form Fields */}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default NewApplication;
