// export default Login;
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import useApi from './useApi/useApi';
import { login } from './apiUrl/apiUrl';

const Login = () => {
    const { post: postLogin, response: loginResponse, error: loginError, loading: loginLoading } = useApi({ url: loginUrl, method: 'POST' }, []);
    const [tcknVkn, setTckn] = useState('');
    const [password, setPassword] = useState('');
    const [loginErrorMessage, setLoginErrorMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await postLogin({ tcknVkn, password });
            
            if (loginError) {
                setLoginErrorMessage('Kullanıcı adı veya parola yanlış.');
            } else if (loginResponse && loginResponse.userCode) {
                // Giriş başarılı olduğunda userCode'u al ve yeni API çağrısı yap
                const userCode = loginResponse.userCode;
                navigate(`/loans/${userCode}`);
            }
        } catch (error) {
            console.error('Giriş sırasında hata meydana geldi!', error);
            setLoginErrorMessage('Giriş sırasında bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    const handleRegisterRedirect = () => {
        navigate('/register'); 
    };

    return (
        <div>
            <h1 align="center">Giriş Yap</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={tcknVkn}
                    onChange={(e) => setTckn(e.target.value)}
                    placeholder="TCKN"
                    required
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Parola"
                    required
                />
                <button type="submit" disabled={loginLoading}>Giriş Yap</button>
            </form>
            {loginErrorMessage && (
                <p className="error">{loginErrorMessage}</p>
            )}
            <br></br>
            <button onClick={handleRegisterRedirect}>Kullanıcı Oluştur</button>
        </div>
    );
};

export default Login;
